using namespace std;
void main()
{  set s;            //定义set容器s
   set::iterator it;    //定义set容器迭代器it
   s.insert(1);
   s.insert(3);
   s.insert(2);
   s.insert(4);
   s.insert(2);
   printf(" s: ");
   for (it=s.begin();it!=s.end();it++)
    printf("%d ",*it);
   printf("\n");
//s:1 2 3 4
   multiset ms;    //定义multiset容器ms
   multiset::iterator mit;
            //定义multiset容器迭代器mit
   ms.insert(1);
   ms.insert(3);
   ms.insert(2);
   ms.insert(4);
   ms.insert(2);
   printf("ms: ");
   for (mit=ms.begin();mit!=ms.end();mit++)
    printf("%d ",*mit);
   printf("\n");
}
